#define   SPI_SR_RXBUFF_1           (0x1u <<  6) /**< \brief (SPI_SR) Both RCR and RNCR has a value of 0. */
#define SPI_SR_TXBUFE               (0x1u <<  7) /**< \brief (SPI_SR) TX Buffer Empty */
